//loading the 'login' angularJS modulevar login = angular.module('login', []);//defining the login controllerlogin.controller('login', function($scope, $http) {	//Initializing the 'invalid_login' and 'unexpected_error' 	//to be hidden in the UI by setting them true,	//Note: They become visible when we set them to false	$scope.invalid_login = true;	$scope.validlogin = true;	$scope.duplicate_user = true;	$scope.submit = function() {		$http({			method : "POST",			url : '/signin',			data : {				"username" : $scope.username,				"password" : $scope.password			}		}).success(function(response){			if(response.login == "Success")				window.location.assign("/successLogin");			else				$scope.invalid_login = false;			$scope.validlogin = true;		}).error(function(error){			alert("error");		});	};	$scope.register = function() {		$http({			method : "POST",			url : '/afterRegister',			data : {				"username" : $scope.username,				"password" : $scope.password,				"firstname":$scope.firstname,				"lastname":$scope.lastname			}		}).success(function(response){			if(response.login == "Success") {				$scope.duplicate_user = true;				window.location.assign("/");			}			else			$scope.duplicate_user=false;		}).error(function(error){			alert("error");		});	};})